import { useState, useEffect } from "react";
import Footer from "../components/Footer";
import Header from "../components/Header";
import styles from "../styles/HomePage.module.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function HomePage() {
  const navigate = useNavigate();
  const [reviews, setReviews] = useState([]); // State for reviews
  const [currentIndex, setCurrentIndex] = useState(0); // Current index in the carousel

  useEffect(() => {
    // Fetch reviews from the backend
    axios
      .get("http://localhost:5000/reviews") // Adjust the endpoint as per your backend
      .then((response) => {
        setReviews(response.data.reviews); // Assuming the server responds with a `reviews` array
      })
      .catch((error) => {
        console.error("Error fetching reviews:", error);
      });
  }, []);

  const nextReview = () => {
    // Navigate to the next review
    setCurrentIndex((prevIndex) => (prevIndex + 1) % reviews.length);
  };

  const prevReview = () => {
    // Navigate to the previous review
    setCurrentIndex(
      (prevIndex) => (prevIndex - 1 + reviews.length) % reviews.length
    );
  };

  return (
    <div className={styles.container}>
      <Header />
      <div className={styles.heroSection}>
        <div className={styles.heroText}>
          <h1 className={styles.title}>Найкраща піца у вашому місті.</h1>
          <p className={styles.subtitle}>Спробуй!</p>
          <button
            onClick={() => navigate("/menu")}
            className={styles.menu_button}
          >
            Переглянути меню
          </button>
        </div>
      </div>
      <div className={styles.infoText}>
        <p>
          Не втрачайте можливість скуштувати нашу унікальну піцу, приготовану з
          найкращих інгредієнтів. Вибирайте свою улюблену піцу та насолоджуйтеся
          неперевершеним смаком!
        </p>
      </div>
      <p className={styles.feedback_p}>Що кажуть клієнти:</p>
      <div className={styles.carouselContainer}>
        {reviews.length > 0 ? (
          <>
            <button onClick={prevReview} className={styles.arrow}>
              ◀
            </button>
            <div className={styles.reviewCard}>
              <h3>{reviews[currentIndex].name}</h3>
              <p>{reviews[currentIndex].review}</p>
              <p className={styles.reviewDate}>
                {new Date(
                  reviews[currentIndex].reviewCreatedAt
                ).toLocaleDateString()}
              </p>
            </div>
            <button onClick={nextReview} className={styles.arrow}>
              ▶
            </button>
          </>
        ) : (
          <p>Немає відгуків. Будьте першим, хто залишить відгук!</p>
        )}
      </div>
      <Footer />
    </div>
  );
}
