import React, { useState } from "react";
import axios from "axios";
import styles from "../styles/AddPizzaPage.module.css";
import Header from "../components/Header";

export default function AddPizzaPage() {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    imageUrl: "",
    category: "",
  });

  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5000/add-pizza",
        formData
      );
      setSuccess("Піцу успішно додано!");
      setFormData({
        name: "",
        description: "",
        price: "",
        imageUrl: "",
        category: "",
      });
    } catch (err) {
      setError("Не вдалося додати піцу. Будь ласка, спробуйте ще раз.");
    }
  };

  return (
    <>
      <Header />
      <div className={styles.formContainer}>
        <h2 className={styles.title}>Додати нову піцу</h2>
        {success && <p className={styles.successMessage}>{success}</p>}
        {error && <p className={styles.errorMessage}>{error}</p>}
        <form onSubmit={handleSubmit} className={styles.form}>
          <label className={styles.label}>
            Назва піци:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className={styles.input}
              required
            />
          </label>
          <label className={styles.label}>
            Опис:
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className={styles.textarea}
              required
            />
          </label>
          <label className={styles.label}>
            Ціна (₴):
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleChange}
              className={styles.input}
              required
            />
          </label>
          <label className={styles.label}>
            URL зображення:
            <input
              type="text"
              name="imageUrl"
              value={formData.imageUrl}
              onChange={handleChange}
              className={styles.input}
            />
          </label>
          <label className={styles.label}>
            Категорія:
            <select
              name="category"
              value={formData.category}
              onChange={handleChange}
              className={styles.select}
              required
            >
              <option value="">Виберіть категорію</option>
              <option value="vegetarian">Вегетаріанська</option>
              <option value="spicy">Гостра</option>
              <option value="cheese">Сирна</option>
              <option value="meat">М'ясна</option>
            </select>
          </label>
          <button type="submit" className={styles.button}>
            Додати піцу
          </button>
        </form>
      </div>
    </>
  );
}
