import React, { useState } from "react";
import styles from "../styles/RegisterPage.module.css";
// import { auth } from "../firebase";
// import { createUserWithEmailAndPassword, updateProfile } from "firebase/auth";
import { useNavigate } from "react-router-dom";

export default function RegisterPage() {
  const [name, setName] = useState(""); // Added state for name
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();

    // Password validation
    if (password !== confirmPassword) {
      setError("Паролі не співпадають");
      return;
    }

    // Check if the name is entered
    if (!name.trim()) {
      setError("Будь ласка, введіть ваше ім'я");
      return;
    }

    try {
      // Uncomment when Firebase setup is ready
      // Create user in Firebase
      // const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      // const user = userCredential.user;

      // Set the user's display name
      // await updateProfile(user, { displayName: name });

      // Redirect to profile page or home page
      navigate("/profile");
    } catch (error) {
      // Error handling
      if (error.code === "auth/email-already-in-use") {
        setError("Електронна пошта вже використовується");
      } else if (error.code === "auth/invalid-email") {
        setError("Невірний формат електронної пошти");
      } else if (error.code === "auth/weak-password") {
        setError("Пароль має містити щонайменше 6 символів");
      } else {
        setError("Сталася помилка. Спробуйте ще раз");
      }
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.formContainer}>
        <h2 className={styles.title}>Реєстрація</h2>
        <form onSubmit={handleRegister} className={styles.form}>
          {error && <p className={styles.error}>{error}</p>}
          <input
            type="text"
            placeholder="Ваше ім'я" // Added input for name
            value={name}
            onChange={(e) => setName(e.target.value)}
            className={styles.input}
            required
          />
          <input
            type="email"
            placeholder="Електронна пошта"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className={styles.input}
            required
          />
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className={styles.input}
            required
          />
          <input
            type="password"
            placeholder="Підтвердіть пароль"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            className={styles.input}
            required
          />
          <button type="submit" className={styles.button}>
            Зареєструватися
          </button>
        </form>
        <p className={styles.loginLink}>
          Вже маєте акаунт? <a href="/login">Увійдіть</a>
        </p>
      </div>
    </div>
  );
}
