import React, { useState } from "react";
import styles from "../styles/NewsPage.module.css";
import Header from "../components/Header";
import Footer from "../components/Footer";

export default function NewsPage() {
  const [openIndex, setOpenIndex] = useState(null);

  const toggleArticle = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  const articles = [
    {
      title: "Новий рекорд у доставці піци",
      content:
        "Ми доставили гарячу піцу за рекордний час — лише 15 хвилин! Наша команда невпинно працює над вдосконаленням сервісу. Замовляйте свою улюблену піцу вже зараз і переконайтеся самі!Ми доставили гарячу піцу за рекордний час — лише 15 хвилин! Наша команда невпинно працює над вдосконаленням сервісу. Замовляйте свою улюблену піцу вже зараз і переконайтеся самі!",
    },
    {
      title: "Свіжі салати кожного дня",
      content:
        "У нашому меню з’явилися нові салати з найсвіжіших інгредієнтів. Ми подбали про те, щоб кожна страва була не тільки смачною, але й корисною. Спробуйте наші новинки та відчуйте різницю!",
    },
    {
      title: "Секрет ідеальної піци",
      content:
        "Наш шеф-кухар розповів секрет приготування ідеальної піци: це поєднання найкращих інгредієнтів, правильна температура печі та краплинка любові. Не втрачайте нагоди насолодитися нашими кулінарними шедеврами!",
    },
    {
      title: "Безкоштовна доставка до кінця місяця",
      content:
        "Замовляйте будь-яку страву з нашого меню та отримуйте безкоштовну доставку. Пропозиція діє до кінця цього місяця. Не зволікайте, адже час обмежений!",
    },
    {
      title: "Топ-5 найпопулярніших страв",
      content:
        "До вашої уваги рейтинг наших найпопулярніших страв: Маргарита, Пепероні, Грецький салат, Лазанья та Тірамісу. Спробуйте кожну з них і знайдіть свого фаворита!",
    },
    {
      title: "Нове меню напоїв",
      content:
        "Ми оновили наш асортимент напоїв! Тепер у нас є освіжаючі лимонади, ароматна кава та багато іншого. Замовляйте напої разом із улюбленими стравами для повного задоволення.",
    },
    {
      title: "Історія нашого ресторану",
      content:
        "Наш ресторан розпочав свій шлях як маленька піцерія у 2010 році. Завдяки вашій підтримці ми зросли та стали одним із найпопулярніших закладів у місті. Дякуємо, що ви з нами!",
    },
  ];

  return (
    <>
      <Header />
      <div className={styles.container}>
        <h1 className={styles.title}>Новини та статті</h1>
        <div className={styles.newsList}>
          {articles.map((article, index) => (
            <div key={index} className={styles.newsCard}>
              <h2
                className={styles.newsTitle}
                onClick={() => toggleArticle(index)}
              >
                {article.title}
              </h2>
              <div
                className={`${styles.newsContent} ${
                  openIndex === index ? styles.open : ""
                }`}
              >
                <p>{article.content}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}
