import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "../styles/AddPizzaPage.module.css";
import Header from "../components/Header";
import { useParams, useNavigate } from "react-router-dom";

export default function AddPizzaPage({ isEdit = false }) {
  const { id } = useParams(); // Отримуємо ID піци для редагування (якщо є)
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    imageUrl: "",
    category: "",
  });

  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  // Завантаження даних піци, якщо редагування
  useEffect(() => {
    if (isEdit && id) {
      const fetchPizza = async () => {
        try {
          const response = await axios.get(
            `http://localhost:5000/pizzas/${id}`
          );
          setFormData(response.data); // Завантажуємо дані в форму
        } catch (err) {
          setError("Не вдалося завантажити дані піци.");
        }
      };

      fetchPizza();
    }
  }, [isEdit, id]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEdit) {
        // Редагування піци
        await axios.put(`http://localhost:5000/pizzas/${id}`, formData);
        setSuccess("Піцу успішно оновлено!");
      } else {
        // Додавання нової піци
        await axios.post("http://localhost:5000/add-pizza", formData);
        setSuccess("Піцу успішно додано!");
        setFormData({
          name: "",
          description: "",
          price: "",
          imageUrl: "",
          category: "",
        });
      }
      setTimeout(() => navigate("/menu"), 2000); // Перенаправлення до меню через 2 секунди
    } catch (err) {
      setError(
        isEdit
          ? "Не вдалося оновити піцу. Будь ласка, спробуйте ще раз."
          : "Не вдалося додати піцу. Будь ласка, спробуйте ще раз."
      );
    }
  };

  return (
    <>
      <Header />
      <div className={styles.formContainer}>
        <h2 className={styles.title}>
          {isEdit ? "Редагувати піцу" : "Додати нову піцу"}
        </h2>
        {success && <p className={styles.successMessage}>{success}</p>}
        {error && <p className={styles.errorMessage}>{error}</p>}
        <form onSubmit={handleSubmit} className={styles.form}>
          <label className={styles.label}>
            Назва піци:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className={styles.input}
              required
            />
          </label>
          <label className={styles.label}>
            Опис:
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className={styles.textarea}
              required
            />
          </label>
          <label className={styles.label}>
            Ціна (₴):
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleChange}
              className={styles.input}
              required
            />
          </label>
          <label className={styles.label}>
            URL зображення:
            <input
              type="text"
              name="imageUrl"
              value={formData.imageUrl}
              onChange={handleChange}
              className={styles.input}
            />
          </label>
          <label className={styles.label}>
            Категорія:
            <select
              name="category"
              value={formData.category}
              onChange={handleChange}
              className={styles.select}
              required
            >
              <option value="">Виберіть категорію</option>
              <option value="vegetarian">Вегетаріанська</option>
              <option value="spicy">Гостра</option>
              <option value="cheese">Сирна</option>
              <option value="meat">М'ясна</option>
            </select>
          </label>
          <button type="submit" className={styles.button}>
            {isEdit ? "Оновити піцу" : "Додати піцу"}
          </button>
        </form>
      </div>
    </>
  );
}
