import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "../styles/NewsPage.module.css";
import Header from "../components/Header";
import Footer from "../components/Footer";

export default function NewsPage() {
  const [news, setNews] = useState([]);
  const [activeIndex, setActiveIndex] = useState(null);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        // Для примера создаем несколько новостей локально
        const newsData = [
          {
            id: 1,
            title: "Новая коллекция пиццы",
            description: "Мы добавили несколько новых видов пицц в меню. Обновите свой заказ!",
            details: "Пицца с морепродуктами, пицца с карбонарой и другие новинки ждут вас в меню!",
          },
          {
            id: 2,
            title: "Акция на напитки",
            description: "При заказе пиццы вы получаете скидку 10% на напитки.",
            details: "Не упустите шанс заказать напитки по скидке, пока акция действует!",
          },
          {
            id: 3,
            title: "Время доставки уменьшено",
            description: "Мы улучшили время доставки, теперь мы привезем ваш заказ быстрее.",
            details: "Обновлен логистический процесс, теперь среднее время доставки — 30 минут.",
          },
          {
            id: 4,
            title: "Новое в меню: Салаты",
            description: "В меню теперь есть новый выбор салатов! Попробуйте свежие новинки.",
            details: "Салаты с курицей, тунцом, овощами — для любителей легких и полезных блюд.",
          },
          {
            id: 5,
            title: "Акция для студентов",
            description: "Студенты могут получить скидку 20% на все заказы в будние дни.",
            details: "Для студентов — скидка 20% при предъявлении студенческого билета.",
          },
          {
            id: 6,
            title: "Летнее меню",
            description: "Летние предложения пиццы с фруктами. Попробуйте вкусные новинки.",
            details: "Пиццы с манго, ананасами и другими фруктами — уникальные летние сочетания.",
          },
          {
            id: 7,
            title: "Мы расширяем сеть",
            description: "Открытие нового ресторана в центре города. Смотрите на нашем сайте подробности!",
            details: "Скоро откроется новый ресторан, следите за новостями на сайте и в соцсетях!",
          }
        ];

        setNews(newsData);
      } catch (error) {
        console.error("Failed to fetch news:", error);
      }
    };

    fetchNews();
  }, []);

  const toggleCard = (index) => {
    setActiveIndex((prevIndex) => (prevIndex === index ? null : index));
  };

  return (
    <>
      <Header />
      <div className={styles.container}>
        <h1 className={styles.title}>Новости</h1>
        <div className={styles.newsList}>
          {news.map((item, index) => (
            <div
              key={item.id}
              className={`${styles.newsCard} ${activeIndex === index ? styles.open : ""}`}
            >
              <div className={styles.cardHeader} onClick={() => toggleCard(index)}>
                <h3 className={styles.newsTitle}>{item.title}</h3>
                <p className={styles.newsDescription}>{item.description}</p>
              </div>
              {activeIndex === index && (
                <div className={styles.cardDetails}>
                  <p>{item.details}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}
