import React, { useState } from "react";
import styles from "../styles/ProfilePage.module.css";
import { useUser } from "../context/UserContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUserCircle,
  faEdit,
  faSignOutAlt,
} from "@fortawesome/free-solid-svg-icons";

export default function ProfilePage() {
  const { user, updateUser, logout } = useUser();
  console.log(user);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user.displayName || "",
    email: user.email || "",
    // Додайте інші поля профілю за потреби
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Виклик функції для оновлення профілю користувача
    updateUser(formData);
    setIsEditing(false);
  };

  return (
    <div className={styles.container}>
      <h1>Мій профіль</h1>
      <div className={styles.profileCard}>
        <div className={styles.avatar}>
          <FontAwesomeIcon icon={faUserCircle} />
        </div>
        {isEditing ? (
          <form className={styles.form} onSubmit={handleSubmit}>
            <label>
              Ім'я:
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
            </label>
            <label>
              Електронна пошта:
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
            </label>

            <div className={styles.buttonGroup}>
              <button type="submit" className={styles.saveButton}>
                Зберегти зміни
              </button>
              <button
                type="button"
                className={styles.cancelButton}
                onClick={() => setIsEditing(false)}
              >
                Скасувати
              </button>
            </div>
          </form>
        ) : (
          <div className={styles.profileInfo}>
            <p>
              <strong>Ім'я:</strong> {user.name}
            </p>
            <p>
              <strong>Електронна пошта:</strong> {user.email}
            </p>

            <div className={styles.buttonGroup}>
              <button
                className={styles.editButton}
                onClick={() => setIsEditing(true)}
              >
                <FontAwesomeIcon icon={faEdit} /> Редагувати профіль
              </button>
              <button className={styles.logoutButton} onClick={logout}>
                <FontAwesomeIcon icon={faSignOutAlt} /> Вийти з акаунту
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
