import React, { useState } from "react";
import styles from "../styles/CartDropdown.module.css";
import { useCart } from "../context/CartContext";
import { useNavigate } from "react-router-dom";
import { useUser } from "../context/UserContext";

export default function CartDropdown() {
  const [isOpen, setIsOpen] = useState(false);
  const { cartItems, total, removeFromCart, updateQuantity } = useCart();
  const navigate = useNavigate();
  const { user } = useUser();

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleCheckout = () => {
    if (user) {
      navigate("/checkout");
    } else {
      navigate("/login");
    }
  };

  return (
    <div className={styles.cartContainer}>
      <button onClick={toggleDropdown} className={styles.cartButton}>
        üõí –ö–æ—à–∏–∫ ({cartItems.reduce((sum, item) => sum + item.quantity, 0)})
      </button>
      {isOpen && (
        <div className={styles.dropdown}>
          {cartItems.length === 0 ? (
            <p className={styles.empty}>–ö–æ—à–∏–∫ –ø–æ—Ä–æ–∂–Ω—ñ–π</p>
          ) : (
            <>
              <ul className={styles.cartList}>
                {cartItems.map((item) => (
                  <li key={item.id} className={styles.cartItem}>
                    <img
                      src={item.imageUrl}
                      alt={item.name}
                      className={styles.image}
                    />
                    <div className={styles.details}>
                      <p className={styles.name}>{item.name}</p>
                      <p className={styles.price}>
                        {item.price} ‚Ç¥ √ó {item.quantity}
                      </p>
                    </div>
                    <div className={styles.quantityControls}>
                      <button
                        onClick={() => updateQuantity(item.id, -1)}
                        className={styles.quantityButton}
                      >
                        ‚àí
                      </button>
                      <span className={styles.quantity}>{item.quantity}</span>
                      <button
                        onClick={() => updateQuantity(item.id, 1)}
                        className={styles.quantityButton}
                      >
                        +
                      </button>
                    </div>
                    <button
                      onClick={() => removeFromCart(item.id)}
                      className={styles.removeButton}
                    >
                      ‚ùå
                    </button>
                  </li>
                ))}
              </ul>
              <div className={styles.total}>
                <p>–ó–∞–≥–∞–ª–æ–º: {total} ‚Ç¥</p>
              </div>
              <button
                onClick={handleCheckout}
                className={styles.checkoutButton}
              >
                –û—Ñ–æ—Ä–º–∏—Ç–∏ –∑–∞–º–æ–≤–ª–µ–Ω–Ω—è
              </button>
            </>
          )}
        </div>
      )}
    </div>
  );
}
