import { useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import styles from "../styles/NewsPage.module.css";

export default function NewsPage() {
  const [activeNews, setActiveNews] = useState(null);

  const newsData = [
    {
      title: "Нова піца в нашому меню!",
      content:
        "Ми раді представити нову піцу з трюфельним соусом! Справжня знахідка для гурманів. Скуштуйте її вже сьогодні в нашому ресторані!",
      date: "20 листопада 2024",
    },
    {
      title: "Ресторан зачиняється на оновлення!",
      content:
        "Наша команда продовжує працювати над поліпшенням обслуговування. Тому ресторан тимчасово зачиняється на декілька днів для технічних робіт.",
      date: "18 листопада 2024",
    },
    {
      title: "Спеціальні знижки на піцу!",
      content:
        "Цей місяць ми даруємо знижки до 20% на всі піци! Не пропустіть шанс відвідати нас і скуштувати ваші улюблені страви за вигідною ціною.",
      date: "15 листопада 2024",
    },
    {
      title: "Зустрічайте нові десерти!",
      content:
        "Ми додали нові смачні десерти в меню! Приходьте спробувати шоколадний фондан та лимонний тарт – обов’язково полюбите!",
      date: "10 листопада 2024",
    },
    {
      title: "Ресторан відкритий для доставки!",
      content:
        "Тепер ви можете замовити доставку своїх улюблених страв безпосередньо додому. Поспішайте зробити замовлення!",
      date: "5 листопада 2024",
    },
    {
      title: "Піца з найкращими інгредієнтами!",
      content:
        "Ми використовуємо тільки найкращі інгредієнти для нашої піци, щоб ви могли насолоджуватися кожним шматочком. Скуштуйте і переконайтеся самі!",
      date: "1 листопада 2024",
    },
    {
      title: "Акція для постійних клієнтів!",
      content:
        "Всі наші постійні клієнти отримують знижку на наступне замовлення. Ставайте частиною нашої родини та отримуйте приємні бонуси!",
      date: "28 жовтня 2024",
    },
  ];

  const handleNewsClick = (index) => {
    setActiveNews(activeNews === index ? null : index);
  };

  return (
    <div className={styles.container}>
      <Header />
      <h1 className={styles.pageTitle}>Новини ресторану</h1>

      <div className={styles.newsContainer}>
        {newsData.map((news, index) => (
          <div
            key={index}
            className={`${styles.newsCard} ${activeNews === index ? styles.active : ""}`}
          >
            <h2
              className={styles.newsTitle}
              onClick={() => handleNewsClick(index)}
            >
              {news.title}
            </h2>
            <p className={styles.newsDate}>{news.date}</p>
            {activeNews === index && <p className={styles.newsContent}>{news.content}</p>}
          </div>
        ))}
      </div>

      <Footer />
    </div>
  );
}
