import { Link } from "react-router-dom";
import styles from "../styles/Header.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPizzaSlice,
  faShoppingCart,
  faUserCircle, // Add User Circle Icon
} from "@fortawesome/free-solid-svg-icons";
import { useUser } from "../context/UserContext";
import CartDropdown from "./CartDropdown";
import { useState } from "react";

export default function Header() {
  const { user, logout } = useUser(); // Include logout function if needed
  const [cartItems, setCartItems] = useState([
    { id: 1, name: "Маргарита", price: 200, imageUrl: "pizza-url-1.jpg" },
    { id: 2, name: "Гавайська", price: 250, imageUrl: "pizza-url-2.jpg" },
  ]);

  const handleRemove = (id) => {
    setCartItems(cartItems.filter((item) => item.id !== id));
  };

  return (
    <div className={styles.container}>
      <div className={styles.linksContainer}>
        {/* Navigation Links */}
        <Link to="/menu" className={styles.link}>
          <p className={styles.titles}>
            <FontAwesomeIcon icon={faPizzaSlice} className={styles.icon} /> Меню
          </p>
        </Link>
        <Link to="/news" className={styles.link}>
          <p className={styles.titles}>Новини</p>
        </Link>
        <Link to="/about" className={styles.link}>
          <p className={styles.titles}>Про нас</p>
        </Link>
      </div>

      {/* If user is logged in */}
      {user ? (
        <div className={styles.userContainer}>
          <CartDropdown cartItems={cartItems} onRemove={handleRemove} />
          <Link to="/profile" className={`${styles.link} ${styles.userIcon}`}>
            <FontAwesomeIcon icon={faUserCircle} /> {user.name || "Мій акаунт"}
          </Link>
        </div>
      ) : (
        <div className={styles.buttonContainer}>
          <Link to="/login" className={`${styles.link} ${styles.button}`}>
            Вхід
          </Link>
          <Link to="/register" className={`${styles.link} ${styles.button}`}>
            Реєстрація
          </Link>
          <Link to="/cart" className={`${styles.link} ${styles.cartButton}`}>
            <FontAwesomeIcon icon={faShoppingCart} /> Кошик
          </Link>
        </div>
      )}
    </div>
  );
}
