import React, { useState } from "react";
import styles from "../styles/CartDropdown.module.css";

export default function CartDropdown({ cartItems, onRemove }) {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={styles.cartContainer}>
      <button onClick={toggleDropdown} className={styles.cartButton}>
        üõí –ö–æ—à–∏–∫ ({cartItems.length})
      </button>
      {isOpen && (
        <div className={styles.dropdown}>
          {cartItems.length === 0 ? (
            <p className={styles.empty}>–ö–æ—à–∏–∫ –ø–æ—Ä–æ–∂–Ω—ñ–π</p>
          ) : (
            <ul className={styles.cartList}>
              {cartItems.map((item, index) => (
                <li key={index} className={styles.cartItem}>
                  <img
                    src={item.imageUrl}
                    alt={item.name}
                    className={styles.image}
                  />
                  <div className={styles.details}>
                    <p className={styles.name}>{item.name}</p>
                    <p className={styles.price}>{item.price} ‚Ç¥</p>
                  </div>
                  <button
                    onClick={() => onRemove(item.id)}
                    className={styles.removeButton}
                  >
                    ‚ùå
                  </button>
                </li>
              ))}
            </ul>
          )}
          <div className={styles.total}>
            <p>
              –ó–∞–≥–∞–ª–æ–º:{" "}
              {cartItems.reduce((total, item) => total + item.price, 0)} ‚Ç¥
            </p>
          </div>
        </div>
      )}
    </div>
  );
}
