import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "../styles/MenuPage.module.css";
import Header from "../components/Header";
import { useCart } from "../context/CartContext";
import Footer from "../components/Footer";
import { useUser } from "../context/UserContext";
import { useNavigate } from "react-router-dom";

export default function MenuPage() {
  const [pizzas, setPizzas] = useState([]);
  const { addToCart } = useCart();
  const { user } = useUser();
  const isAdmin = user?.role === "admin";
  const navigator = useNavigate();

  useEffect(() => {
    const fetchPizzas = async () => {
      try {
        const response = await axios.get("http://localhost:5000/pizzas");
        setPizzas(response.data);
      } catch (error) {
        console.error("Failed to fetch pizzas:", error);
      }
    };

    fetchPizzas();
  }, []);
  const handleAddPizza = () => {
    navigator("/add-pizza");
  };

  const handleEditPizza = (pizzaId) => {
    // Redirect to edit pizza form or open modal
    window.location.href = `/edit-pizza/${pizzaId}`; // Replace with your route or modal logic
  };

  return (
    <>
      <Header />
      <div className={styles.container}>
        <h1 className={styles.title}>Меню</h1>

        {isAdmin && (
          <button onClick={handleAddPizza} className={styles.addPizzaButton}>
            + Додати нове
          </button>
        )}

        <div className={styles.pizzaList}>
          {pizzas.map((pizza) => (
            <div key={pizza.id} className={styles.pizzaCard}>
              <img
                src={pizza.imageUrl}
                alt={pizza.name}
                className={styles.pizzaImage}
              />
              <h3 className={styles.pizzaName}>{pizza.name}</h3>
              <p className={styles.pizzaDescription}>{pizza.description}</p>
              <p className={styles.pizzaPrice}>{pizza.price} грн</p>
              <button
                onClick={() => addToCart(pizza)}
                className={styles.addButton}
              >
                Додати в кошик
              </button>
              {isAdmin && (
                <button
                  onClick={() => handleEditPizza(pizza.id)}
                  className={styles.editButton}
                >
                  ✎ Редагувати
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}
