// src/components/Register.js
import React, { useState } from "react";
import styles from "../styles/RegisterPage.module.css";
// import { auth } from "../firebase";
// import { createUserWithEmailAndPassword } from "firebase/auth";
import { useNavigate } from "react-router-dom";

export default function RegisterPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();

    // // Перевірка пароля
    // if (password !== confirmPassword) {
    //   setError("Паролі не співпадають");
    //   return;
    // }

    // try {
    //   // Створення користувача в Firebase Authentication
    //   await createUserWithEmailAndPassword(auth, email, password);
    //   // Перенаправлення на головну сторінку або сторінку профілю
    //   navigate("/profile");
    // } catch (error) {
    //   // Обробка помилок
    //   if (error.code === "auth/email-already-in-use") {
    //     setError("Електронна пошта вже використовується");
    //   } else if (error.code === "auth/invalid-email") {
    //     setError("Невірний формат електронної пошти");
    //   } else if (error.code === "auth/weak-password") {
    //     setError("Пароль має містити щонайменше 6 символів");
    //   } else {
    //     setError("Сталася помилка. Спробуйте ще раз");
    //   }
  };

  return (
    <div className={styles.container}>
      <div className={styles.formContainer}>
        <h2 className={styles.title}>Реєстрація</h2>
        <form onSubmit={handleRegister} className={styles.form}>
          {error && <p className={styles.error}>{error}</p>}
          <input
            type="email"
            placeholder="Електронна пошта"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className={styles.input}
            required
          />
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className={styles.input}
            required
          />
          <input
            type="password"
            placeholder="Підтвердіть пароль"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            className={styles.input}
            required
          />
          <button type="submit" className={styles.button}>
            Зареєструватися
          </button>
        </form>
        <p className={styles.loginLink}>
          Вже маєте акаунт? <a href="/login">Увійдіть</a>
        </p>
      </div>
    </div>
  );
}
