import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "../styles/LoginPage.module.css";
// import { auth } from "../firebase";
// import { signInWithEmailAndPassword } from "firebase/auth";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Uncomment and use Firebase Authentication when it's set up
      // await signInWithEmailAndPassword(auth, email, password);
      // Redirect to home or profile page
      navigate("/profile");
    } catch (err) {
      // Handle errors from Firebase Authentication
      if (err.code === "auth/user-not-found") {
        setError("Користувач не знайдений");
      } else if (err.code === "auth/wrong-password") {
        setError("Неправильний пароль");
      } else if (err.code === "auth/invalid-email") {
        setError("Невірний формат електронної пошти");
      } else {
        setError("Сталася помилка. Спробуйте ще раз");
      }
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.formContainer}>
        <h2 className={styles.title}>Вхід</h2>
        <form onSubmit={handleLogin} className={styles.form}>
          {error && <p className={styles.error}>{error}</p>}
          <input
            type="email"
            placeholder="Електронна пошта"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className={styles.input}
            required
          />
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className={styles.input}
            required
          />
          <button type="submit" className={styles.button}>
            Увійти
          </button>
        </form>
        <p className={styles.registerLink}>
          Ще не маєте акаунту? <a href="/register">Зареєструйтесь</a>
        </p>
      </div>
    </div>
  );
}
