import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "../styles/NewsPage.module.css";
import Header from "../components/Header";
import Footer from "../components/Footer";

export default function NewsPage() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        // Для примера создаем несколько новостей локально
        const newsData = [
          {
            id: 1,
            title: "Новая коллекция пиццы",
            description: "Мы добавили несколько новых видов пицц в меню. Обновите свой заказ!",
          },
          {
            id: 2,
            title: "Акция на напитки",
            description: "При заказе пиццы вы получаете скидку 10% на напитки.",
          },
          {
            id: 3,
            title: "Время доставки уменьшено",
            description: "Мы улучшили время доставки, теперь мы привезем ваш заказ быстрее.",
          },
          {
            id: 4,
            title: "Новое в меню: Салаты",
            description: "В меню теперь есть новый выбор салатов! Попробуйте свежие новинки.",
          },
          {
            id: 5,
            title: "Акция для студентов",
            description: "Студенты могут получить скидку 20% на все заказы в будние дни.",
          },
          {
            id: 6,
            title: "Летнее меню",
            description: "Летние предложения пиццы с фруктами. Попробуйте вкусные новинки.",
          },
          {
            id: 7,
            title: "Мы расширяем сеть",
            description: "Открытие нового ресторана в центре города. Смотрите на нашем сайте подробности!",
          }
        ];

        setNews(newsData);
      } catch (error) {
        console.error("Failed to fetch news:", error);
      }
    };

    fetchNews();
  }, []);

  return (
    <>
      <Header />
      <div className={styles.container}>
        <h1 className={styles.title}>Новости</h1>
        <div className={styles.newsList}>
          {news.map((item) => (
            <div key={item.id} className={styles.newsCard}>
              <h3 className={styles.newsTitle}>{item.title}</h3>
              <p className={styles.newsDescription}>{item.description}</p>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}
