import React, { useState } from "react";
import styles from "../styles/ProfilePage.module.css";
import { useUser } from "../context/UserContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUserCircle,
  faEdit,
  faSignOutAlt,
} from "@fortawesome/free-solid-svg-icons";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { useNavigate } from "react-router-dom";

export default function ProfilePage() {
  const navigator = useNavigate();
  const { user, updateUser, logout } = useUser();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.displayName || "",
    email: user?.email || "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateUser(formData);
    setIsEditing(false);
  };

  const handleLogout = () => {
    logout();
    navigator("/"); // Перенаправлення на головну сторінку після виходу
  };

  return (
    <>
      <Header />
      <div className={styles.container}>
        <h1>Мій профіль</h1>
        <div className={styles.profileCard}>
          <div className={styles.avatar}>
            <FontAwesomeIcon icon={faUserCircle} />
          </div>
          {user ? ( // Додано перевірку на наявність користувача
            isEditing ? (
              <form className={styles.form} onSubmit={handleSubmit}>
                <label>
                  Ім'я:
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                  />
                </label>
                <label>
                  Електронна пошта:
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                  />
                </label>
                <div className={styles.buttonGroup}>
                  <button type="submit" className={styles.saveButton}>
                    Зберегти зміни
                  </button>
                  <button
                    type="button"
                    className={styles.cancelButton}
                    onClick={() => setIsEditing(false)}
                  >
                    Скасувати
                  </button>
                </div>
              </form>
            ) : (
              <div className={styles.profileInfo}>
                <p>
                  <strong>Ім'я:</strong> {user.displayName}
                </p>
                <p>
                  <strong>Електронна пошта:</strong> {user.email}
                </p>
                <div className={styles.buttonGroup}>
                  <button
                    className={styles.editButton}
                    onClick={() => setIsEditing(true)}
                  >
                    <FontAwesomeIcon icon={faEdit} /> Редагувати профіль
                  </button>
                  <button
                    className={styles.logoutButton}
                    onClick={handleLogout}
                  >
                    <FontAwesomeIcon icon={faSignOutAlt} /> Вийти з акаунту
                  </button>
                </div>
              </div>
            )
          ) : (
            <p>Ви не авторизовані. Будь ласка, увійдіть у свій акаунт.</p>
          )}
        </div>
      </div>
      <Footer />
    </>
  );
}
