import React, { useState } from "react";
import styles from "../styles/NewsPage.module.css";
import Header from "../components/Header";
import Footer from "../components/Footer";

export default function NewsPage() {
  const [openIndex, setOpenIndex] = useState(null);

  const toggleArticle = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  const articles = [
    {
      title: "Новий рекорд у доставці піци",
      content:
        "Ми доставили гарячу піцу за рекордний час — лише 15 хвилин! " +
        "Наші кур’єри щодня вдосконалюють маршрути для максимальної швидкості. " +
        "Завдяки новим технологіям, ми можемо гарантувати збереження тепла та свіжості піци під час доставки. " +
        "Наша мета — зробити вашу вечерю ідеальною. " +
        "Замовляйте піцу і насолоджуйтеся її неперевершеним смаком вже сьогодні!",
    },
    {
      title: "Свіжі салати кожного дня",
      content:
        "Ми ретельно відбираємо тільки найкращі овочі та зелень для наших салатів. " +
        "Кожного дня ви можете насолоджуватися новим поєднанням смаків. " +
        "Здорове харчування — це легко з нашими салатами. " +
        "Спробуйте авторські рецепти та відкрийте для себе світ гастрономічного задоволення. " +
        "Не забудьте додати до замовлення улюблений напій!",
    },
    {
      title: "Секрет ідеальної піци",
      content:
        "Секрет ідеальної піци — це любов до деталей. " +
        "Ми використовуємо тільки італійське борошно та натуральні інгредієнти. " +
        "Кожна піца випікається в дров’яній печі, що додає їй особливого аромату. " +
        "Тонке тісто, хрустка скоринка і соковита начинка — ось чим ми пишаємося. " +
        "Зробіть замовлення і переконайтеся самі!",
    },
    {
      title: "Ексклюзивні пропозиції для гурманів",
      content:
        "Ми зібрали найкращі рецепти з усього світу, щоб ви могли насолоджуватися ними вдома. " +
        "Італійська піца, грецькі салати та азійські соуси — усе це тепер у нашому меню! " +
        "Спробуйте щось нове сьогодні і відкрийте для себе нові кулінарні горизонти. " +
        "Замовляйте прямо зараз і отримайте ексклюзивні знижки на ваші улюблені страви!",
      image: "/images/gourmet_offer.jpg",
    },
    {
      title: "Як обрати ідеальний десерт?",
      content:
        "Десерт — це завершальний штрих до ідеального обіду чи вечері. " +
        "У нашому меню ви знайдете десерти на будь-який смак: від легких фруктових салатів до шоколадних шедеврів. " +
        "Кожен десерт готується з любов'ю, використовуючи найсвіжіші інгредієнти. " +
        "Вибирайте ваш улюблений десерт і насолоджуйтесь неперевершеним смаком.",
      image: "/images/dessert.jpg",
    },
  ];

  return (
    <>
      <Header />
      <div className={styles.container}>
        <h1 className={styles.title}>Новини та статті</h1>
        <div className={styles.newsList}>
          {articles.map((article, index) => (
            <div key={index} className={styles.newsCard}>
              <h2
                className={styles.newsTitle}
                onClick={() => toggleArticle(index)}
              >
                {article.title}
              </h2>
              <div
                className={`${styles.newsContent} ${
                  openIndex === index ? styles.open : ""
                }`}
              >
                {article.image && (
                  <img
                    src={article.image}
                    alt={article.title}
                    className={styles.newsImage}
                  />
                )}
                <p>{article.content}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}
