import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "../styles/MenuPage.module.css";
import Header from "../components/Header";
import { useCart } from "../context/CartContext";
import Footer from "../components/Footer";
import { useUser } from "../context/UserContext";
import { useNavigate } from "react-router-dom";

export default function MenuPage() {
  const [pizzas, setPizzas] = useState([]);
  const [filteredPizzas, setFilteredPizzas] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("");
  const [priceRange, setPriceRange] = useState("");
  const { addToCart } = useCart();
  const { user } = useUser();
  const isAdmin = user?.role === "admin";
  const navigator = useNavigate();
  const [sortOrder, setSortOrder] = useState("asc");

  useEffect(() => {
    const fetchPizzas = async () => {
      try {
        const response = await axios.get("http://localhost:5000/pizzas");
        setPizzas(response.data);
        setFilteredPizzas(response.data); // Set pizzas initially as all
      } catch (error) {
        console.error("Failed to fetch pizzas:", error);
      }
    };

    fetchPizzas();
  }, []);

  // Handle changes in search term
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Handle category filter change
  const handleCategoryChange = (e) => {
    setCategoryFilter(e.target.value);
  };

  // Handle price range filter change
  const handlePriceChange = (e) => {
    setPriceRange(e.target.value);
  };

  // Filter pizzas based on search term, category, and price
  useEffect(() => {
    let filtered = pizzas;

    // Filter by search term (case-insensitive)
    if (searchTerm) {
      filtered = filtered.filter(
        (pizza) =>
          pizza.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          pizza.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by category
    if (categoryFilter) {
      filtered = filtered.filter((pizza) =>
        pizza.category.toLowerCase().includes(categoryFilter.toLowerCase())
      );
    }
    const handleSortChange = (e) => {
      setSortOrder(e.target.value);
    };

    // Filter by price range
    if (priceRange) {
      const [minPrice, maxPrice] = priceRange.split("-");
      filtered = filtered.filter((pizza) => {
        const pizzaPrice = pizza.price;
        if (minPrice && maxPrice) {
          return (
            pizzaPrice >= parseInt(minPrice) && pizzaPrice <= parseInt(maxPrice)
          );
        } else if (minPrice) {
          return pizzaPrice >= parseInt(minPrice);
        } else if (maxPrice) {
          return pizzaPrice <= parseInt(maxPrice);
        }
        return true;
      });
    }

    setFilteredPizzas(filtered);
  }, [searchTerm, categoryFilter, priceRange, pizzas]);

  const handleAddPizza = () => {
    navigator("/add-pizza");
  };

  const handleEditPizza = (pizzaId) => {
    window.location.href = `/edit-pizza/${pizzaId}`;
  };

  return (
    <>
      <Header />
      <div className={styles.container}>
        <h1 className={styles.title}>Меню</h1>

        {isAdmin && (
          <button onClick={handleAddPizza} className={styles.addPizzaButton}>
            + Додати нове
          </button>
        )}

        <div className={styles.filters}>
          {/* Search input */}
          <input
            type="text"
            placeholder="Пошук за назвою або описом"
            value={searchTerm}
            onChange={handleSearchChange}
            className={styles.searchInput}
          />

          {/* Category Filter */}
          <select
            value={categoryFilter}
            onChange={handleCategoryChange}
            className={styles.filterSelect}
          >
            <option value="">Виберіть категорію</option>
            <option value="vegetarian">Вегетаріанська</option>
            <option value="meat">М'ясна</option>
            <option value="spicy">Пікантна</option>
            <option value="seafood">Морська</option>
          </select>

          {/* Price Range Filter */}
          <select
            value={priceRange}
            onChange={handlePriceChange}
            className={styles.filterSelect}
          >
            <option value="">Ціна</option>
            <option value="0-100">До 100 грн</option>
            <option value="100-200">100-200 грн</option>
            <option value="200-">Більше 200 грн</option>
          </select>
        </div>

        <div className={styles.pizzaList}>
          {filteredPizzas.map((pizza) => (
            <div key={pizza.id} className={styles.pizzaCard}>
              <img
                src={pizza.imageUrl}
                alt={pizza.name}
                className={styles.pizzaImage}
              />
              <h3 className={styles.pizzaName}>{pizza.name}</h3>
              <p className={styles.pizzaDescription}>{pizza.description}</p>
              <p className={styles.pizzaPrice}>{pizza.price} грн</p>
              <div className={styles.buttons}>
                <button
                  onClick={() => addToCart(pizza)}
                  className={styles.addButton}
                >
                  Додати в кошик
                </button>
                {isAdmin && (
                  <button
                    onClick={() => handleEditPizza(pizza.id)}
                    className={styles.editButton}
                  >
                    ✎ Редагувати
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}
