import { useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import styles from "../styles/NewsPage.module.css";

export default function NewsPage() {
  const [activeNews, setActiveNews] = useState(null);

  const newsData = [
    {
      title: "Нова піца в нашому меню!",
      content:
        "Ми раді презентувати нову піцу, яка стане справжнім хітом нашого меню. Вона приготована з найкращих інгредієнтів та має неповторний смак!",
      date: "20 листопада 2024",
    },
    {
      title: "Ресторан зачиняється на оновлення!",
      content:
        "Наш ресторан на декілька днів закривається для проведення технічних робіт. Вибачте за тимчасові незручності. Ми повернемося з новими можливостями!",
      date: "18 листопада 2024",
    },
    {
      title: "Спеціальні акції для наших клієнтів!",
      content:
        "Тільки цього місяця ви можете скористатися знижками на деякі наші страви! Переконайтесь, що не пропустили шанс скуштувати улюблені страви по акційним цінам.",
      date: "15 листопада 2024",
    },
  ];

  const handleNewsClick = (index) => {
    setActiveNews(activeNews === index ? null : index);
  };

  return (
    <div className={styles.container}>
      <Header />
      <div className={styles.heroSection}>
        <h1 className={styles.pageTitle}>Новини ресторану</h1>
      </div>

      <div className={styles.newsContainer}>
        {newsData.map((news, index) => (
          <div
            key={index}
            className={`${styles.newsCard} ${activeNews === index ? styles.active : ""}`}
          >
            <h2
              className={styles.newsTitle}
              onClick={() => handleNewsClick(index)}
            >
              {news.title}
            </h2>
            <p className={styles.newsDate}>{news.date}</p>
            {activeNews === index && <p className={styles.newsContent}>{news.content}</p>}
          </div>
        ))}
      </div>

      <Footer />
    </div>
  );
}
